import React, { useState } from "react";

const BinPutAway = () => {
  const [data, setData] = useState();
  const [inputValue, setInputValue] = useState("");
  const [error, setError] = useState("");

  const handelSubmit = async (e) => {
    e.preventDefault();

    if (inputValue.length < 1) {
      setError("Enter valid bin number!");
      return;
    }

    const response = await fetch("/api/locations/" + inputValue);

    const json = await response.json();

    if (!response.ok) {
      setError(json.error);
    }
    if (response.ok) {
      setData(json);
    }
  };

  return (
    <div className="select-bin-page">
      <form onSubmit={handelSubmit} className="item-search__form">
        <label htmlFor="item-input">Select Bin:</label>
        <input
          type="text"
          value={inputValue}
          onChange={(e) => {
            setInputValue(e.target.value.toUpperCase());
          }}
          id="item-input"
        />
        <button type="submit">Search</button>
        {error && <div className="error-message">{error}</div>}
      </form>
      {data && (
        <div className="items-list" key={data._id}>
          <div className="items-list__header">
            <p>Bin</p>
            <p>part</p>
            <p>qty</p>
          </div>
          {data.map((loc) => (
            <div
              className="items-list__item hover"
              key={loc._id}
            >
              <p>{loc.title}</p>
              {loc.items.map((part) => {
                  return (
                    <React.Fragment key={part._id}>
                      {" "}
                      <p>{part.title}</p> <p>{part.qty}</p>
                    </React.Fragment>
                  );
              })}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default BinPutAway;
