import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const PickBin = () => {
  
  const { item, bin} = useParams()
  const [inputValue, setInputValue] = useState('')
  const [data, setData] = useState()
  const [error, setError] = useState()
  const [binTitle, setBinTitle] = useState()

useEffect(() => {
  const fetchData = async () => {
    try{
      const response = await fetch(`/api/locations/${bin}`)
      const json = await response.json()
      if(!response.ok) {
        setError(json.error)
      }
      if(response.ok) {
        setError(null)
        setBinTitle(json.title)
        const part = json.items.find(i => i.title === item)
        setData(part)
      }
    } catch(err) {

    }
  }

  fetchData()
}, [])

const handleSubmit = async (e) => {
  e.preventDefault()
  if(inputValue === '' || inputValue < 0) {
    setError('Enter amount you want to pick')
    return
  }

}

  return ( 
    <div className='pick-bin-page'>
      <form onSubmit={handleSubmit} className='pick-bin-page__form'>
        <div className='pick-bin-page__form-input-group'>
          {error && <div className='error'>{error}</div>}
          <label htmlFor='input'>Quantity To Be Picked:</label>
          <input type='number' id='input' value={inputValue} onChange={handleInputChange}/>
          <button type='submit'>Submit</button>
        </div>
      </form>
      {console.log(data)}
           {data && <div className='bins-list' key={data._id}>
              <div className='bins-list__header'>
                <p>Bin</p>
                <p>part</p>
                <p>qty</p>
              </div>
              <div className='bins-list__bin'>
                <p>{binTitle}</p>
                <p>{data.title}</p>
                <p>{data.qty}</p>
              </div>
            </div>
           
           } 
    </div> );
}
 
export default PickBin;
