import React from 'react';
const ItemsList = ({item, items}) => {

  const renderQty = (location) => {
    location.items.forEach(element => {
      if(element.title === item) {
        return element.qty
      }
    });
  }

  const renderLocations = items.map( location => (<div className='items-list__item' key={location._id}>
    <div>
      <p>{location.title}</p>
    
    </div>
  </div>))

  return ( 
    <div className='items-list'>
       <header className='item-list__header'>
         <h3>Bin</h3>
         <h3>Quantity</h3>
      </header>
      {/* {renderLocations && renderLocations} */}
      {
        items.map( location => (<div className='items-list__item' key={location._id}>
        <div>
          <p>{location.title}</p>
          {renderQty(location)}
        </div>
      </div>))
      }
    </div> 
  );
}
 
export default ItemsList;
