      <button type="submit">Search</button>
      {error && <div className='error-message'>
      {error}
    </div>}
    </form>
    {itemQty && <div className='item-search__total-qty'>Total on Stock: {itemQty}</div>}
 <div className='items-list'>
  <div className='items-list__header'>
    <p>Bin</p>
