import React from 'react';

const StepCounter = (steps) => {


  const renderSteps = () => {
    for(let i = 1; i < steps; i++) {
      if(i === steps) {
        return (
          <div className='step-wrapper'>
          <div className='step'>{i}</div>
        </div>
        )
      }
      return (
        <div className='step-wrapper'>
          <div className='step'>{i}</div>
          <div className='step__bar'></div>
        </div>
      )
    }
  }
  return ( 
    <div className='step-counter'>
      {console.log(steps)}
     {renderSteps()}
    </div>
   );
}
 
export default StepCounter;
