    <div>
      <p>{location.title}</p>
      {
        location.items.forEach(element => {
          <p>{element.qty}</p>
          if(element.title === item) {
            console.log(element.qty)
            return 
          }
        })
        }
      
    </div>
  </div>))

  return ( 
    <div className='items-list'>
       <header className='item-list__header'>
         <h3>Bin</h3>
         <h3>Quantity</h3>
      </header>
      {renderLocations && renderLocations}
    </div> 
  );
}
 
export default ItemsList;
